create table PLANTNAMES (GENERICNAME varchar(20), SPECIESNAME varchar(20), FAMILY varchar(20), COMMONNAME varchar(20), PRIMARY KEY (GENERICNAME, SPECIESNAME));
create table CUSTOMERLOAN (CUSTOMERID int, LOANID varchar(20), ELIGIBLEAMOUNT DOUBLE(8,2), AVAILEDAMOUNT  DOUBLE(8,2), TENUREINYEARS FLOAT(2,1), PRIMARY KEY (CUSTOMERID, LOANID));